targets.expr:
sum by (instance, version, tier) (
  label_set(
    vm_app_version{job=~"$job",instance=~"$instance_select_all"}
      and on(instance,version)
    vm_app_version{job=~"$job",instance=~"$instance_insert_all"} * 0 + 1,
    "tier","select,insert"
  )
or
  label_set(
    vm_app_version{job=~"$job",instance=~"$instance_select_all"}
      unless on(instance,version)
    vm_app_version{job=~"$job",instance=~"$instance_insert_all"} * 0 + 1,
    "tier","select"
  )
or
  label_set(
    vm_app_version{job=~"$job",instance=~"$instance_insert_all"}
      unless on(instance,version)
    vm_app_version{job=~"$job",instance=~"$instance_select_all"} * 0 + 1,
    "tier","insert"
  )
or
  label_set(
    vm_app_version{job=~"$job",instance=~"$instance_storage_all"} * 0 + 1,
    "tier","storage"
  )
or
  label_set(
    (
      vm_app_version{job=~"$job",instance=~"$instance"}
      unless on(instance,version)
      (
        vm_app_version{job=~"$job",instance=~"$instance_select_all"}
        or
        vm_app_version{job=~"$job",instance=~"$instance_insert_all"}
        or
        vm_app_version{job=~"$job",instance=~"$instance_storage_all"}
      )
    ) * 0 + 1,
    "tier","unknown"
  )
)

sum (
  min_over_time(
    up{job=~"$job", instance=~"$instance_select_all"}[$__rate_interval]
  )
  unless on(instance)
  up{job=~"$job", instance=~"$instance_insert_all"}
)

sum (
  min_over_time(
    up{job=~"$job", instance=~"$instance_insert_all"}[$__rate_interval]
  )
  unless on(instance)
  up{job=~"$job", instance=~"$instance_select_all"}
)

sum(
  min_over_time(
    up{job=~"$job", instance=~"$instance_select_all"}[$__rate_interval]
  )
  and on(instance)
  up{job=~"$job", instance=~"$instance_insert_all"}
)

quantile(0.5,
    rate(process_cpu_seconds_total{job=~"$job", instance=~"$instance_storage", instance=~"$instance"}[$__rate_interval])
    /
    process_cpu_cores_available{job=~"$job", instance=~"$instance_storage", instance=~"$instance"}
)
